import React, { useReducer, useContext } from 'react'
import { ThemeContext } from './App'

export default function ChildApp () {
  /**
   * useContext 如果页面组件层级深并且需要子组件触发state的变化
   * @param 
   */
  return <p>{useContext(ThemeContext)}</p>
}
