import React, { useReducer } from "react"
import { RESET, ADD } from "../constant"

const initialState = { count: 0 }

export function reducer (state = initialState, action) {
  switch (action.type) {
    case RESET: 
      return initialState
    case ADD:
      return { count: state.count + 1 }
    default: 
      return state
  }
}
