export default function EffectHook () {
  const [color, setColor] = useState('red')

  useEffect(() => {
    changeColorGreen()
  }, [color])

  const changeColorGreen = () => {
    setColor('green')
  }

  return (
    <div>
      <p style={{color}}>color</p>
    </div>
  )
}
