import React, { useState } from 'react'

export default function HookUseState () {
  /**
   * useState（代替state&setState）
   * @param {Any} 必选 默认值
   */
  const [name, setName] = useState()

  return <div>
    <p>hello {name}</p>
    <button onClick={() => { setName(name + '哈') }}>哈</button>
  </div>
}
