import React, { useState, useEffect } from 'react';

export default function EffectHook () {

  useEffect(() => {
    // 给window绑定点击事件
    window.addEventListener('click', handleClick)

    return () => {
      // 给window移除点击事件
      // 在组件被销毁时会自动执行这个函数
      window.addEventListener('click', handleClick)
    }
  })

  const handleClick = () => {
    console.log('鼠标点击')
  }

  return 
}
