import React, { useState, useEffect } from 'react'

// 传入的第一个参数，可以 return 一个函数，在组件被销毁时，会自动执行这个函数。
export default function HookUseEffectReturnChild () {

  useEffect(() => {
    // 给window绑定点击事件
    window.addEventListener('click', handleClick)

    return () => {
      // 给window移除点击事件
      window.addEventListener('click', handleClick)
    }
  })

  const handleClick = () => {
    console.log('鼠标点击')
  }

  return <div style={{width: '30px', height: '30px'}}>
    <img src='https://img.ikstatic.cn/MTU2NTY4MTQyOTYxNSMxODgjanBn.jpg' />
  </div>
}
