export default function EffectHook () {
  const [color, setColor] = useState('yellow')

  useEffect(() => {
    changeColorGreen()
  }, [color])

  const changeColorRed = () => {
    setColor('red')
  }

  const changeColorGreen = () => {
    setColor('green')
  }

  return (
    <div>
      <p style={`background: ${color}`}>color</p>
      <p style={`background: ${color}`}>coloruseEffect</p>
      <button onClick={changeColorRed}>
        Click me
      </button>
    </div>
  )
}
