import React, { useState, useEffect } from 'react'
import PropTypes from 'prop-types'
import { Form, Input, Select, DatePicker, Button, Col, Row } from 'antd'
import numCommaReplace from 'ik-utils/lib/admin/numCommaReplace'
import filterOption from 'ik-utils/lib/admin/filterOption'

import { timeFormat, momentToTime } from '../constants/timeFormat'
import { selectList, multipleSelectList } from '../constants/selectLists'

export default function AntdHooksFilter (props) {
  const {
    routeAuthority,
    buttonCtrl,
    form: {
      getFieldDecorator,
      getFieldsValue
    },
    searchParams: {
      uid,
      date,
      search1,
      search2,
      search3
    },
    searchParams,
    handleSearch,
    handleAdd,
    handleExport
  } = props

  useEffect(() => {
    handleSearch(searchParamsFilter())
  }, [])

  const searchParamsFilter = () => {
    const filterParams = getFieldsValue()
    const { date, search2, search3 } = filterParams

    const searchParamsAll = Object.assign({}, searchParams, filterParams, {
      start_date: momentToTime(date[0]),
      end_date: momentToTime(date[1]),
      search2: search2.join(),
      search3: search3 || ''
    })

    delete searchParamsAll.date

    return searchParamsAll
  }

  const handleButtonDisabled = key => routeAuthority.indexOf(buttonCtrl[key].id) < 0

  return (
    <Form layout="inline">
      <Row style={styles.form}><p style={styles.title}>查询条件</p></Row>
      <Row style={styles.search}>
        <Form.Item label="UID" style={styles.item}>
          {
            getFieldDecorator('uid', {
              initialValue: uid,
              normalize: (value, prevValue, allValues) => {
                return numCommaReplace(allValues.uid)
              }
            })(
              <Input
                placeholder="请输入uid，多个uid以英文逗号隔开"
                style={{ width: '240px' }}
              />
            )
          }
        </Form.Item>

        <Form.Item label="日期" style={styles.item}>
          {
            getFieldDecorator('date', {
              initialValue: date
            })(
              <DatePicker.RangePicker
                format={timeFormat}
                style={{ width: '240px' }}
              />
            )
          }
        </Form.Item>

        <Form.Item label="查询条件1" style={styles.item}>
          {
            getFieldDecorator('search1', {
              initialValue: search1
            })(
              <Input
                placeholder="请输入查询条件1"
                style={{ width: '200px' }}
              />
            )
          }
        </Form.Item>

        <Form.Item label="查询条件2" style={styles.item}>
          {
            getFieldDecorator('search2', {
              initialValue: search2
            })(
              <Select
                allowClear
                showSearch
                mode="multiple"
                style={{ width: '200px' }}
                placeholder="请选择查询条件2"
                filterOption={filterOption}
              >
                {
                  multipleSelectList.map(item =>
                    <Select.Option
                      key={item.value}
                      value={`${item.value}`}
                    >
                      { item.label }
                    </Select.Option>
                  )
                }
              </Select>
            )
          }
        </Form.Item>

        <Form.Item label="查询条件3" style={styles.item}>
          {
            getFieldDecorator('search3', {
              initialValue: search3
            })(
              <Select
                allowClear
                showSearch
                style={{ width: '200px' }}
                placeholder="请选择查询条件3"
                filterOption={filterOption}
              >
                {
                  selectList.map(item =>
                    <Select.Option
                      key={item.value}
                      value={`${item.value}`}
                    >
                      { item.label }
                    </Select.Option>
                  )
                }
              </Select>
            )
          }
        </Form.Item>
      </Row>
      <Row style={styles.handle}>
        <p style={styles.title}>操作</p>

        <Button
          type="primary"
          style={styles.btn}
          onClick={() => handleSearch(searchParamsFilter())}
          // disabled={handleButtonDisabled('SEARCH')}
        >
          查询
        </Button>

        <Button
          type="primary"
          style={styles.btn}
          onClick={handleAdd}
          // disabled={handleButtonDisabled('ADD')}
        >
          新增
        </Button>

        <Button
          type="primary"
          style={styles.btn}
          onClick={() => handleExport(searchParamsFilter())}
          // disabled={handleButtonDisabled('EXPORT')}
        >
          导出
        </Button>
      </Row>
    </Form>
  )
}

const styles = {
  title: {
    display: 'inline-block',
    margin: 0,
    fontSize: '16px',
    lineHeight: '24px',
  },
  search: {
    margin: '10px 0',
  },
  item: {
    margin: '10px',
  },
  btn: {
    marginLeft: '12px',
  },
}
